sources.src/sass/base.scss:
:root {
  // Color
  --primary-main-color: #a5c2b8;
  --primary-dark-color: #4f8f78;
  --primary-light-color: #e8f5e9;
  --secondary-main-color: #ffa726;
  --secondary-dark-color: #c8b900;
  --secondary-light-color: #fffde7;

  // Font size
  --font-large-size: 1.75rem;
  --font-medium-size: 1.5rem;
  --font-regular-size: 1rem;
  --font-small-size: 0.75rem;
  --font-micro-size: 0.5rem;

  //padding
  --basic-padding: 1rem;

  //border radius
  --basic-radius: 10px;
}
html {
  @include respond(tab-land) {
    font-size: 87.5%;
  }
  @include respond(tab-port) {
    font-size: 62.5%;
  }
  @include respond(mobile) {
    font-size: 50%;
  }
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
body {
  font-family: 'Work Sans', sans-serif;
}

.container {
  max-width: 80%;
  min-height: 100vh;
  margin: 2rem auto;
  @include respond(tab-land) {
    max-width: 90%;
  }
}

main {
  display: flex;
  flex-direction: row;
}

h1 {
  font-size: var(--font-large-size);
  color: var(--primary-dark-color);
  margin: 1rem 0;
}

h2 {
  font-size: var(--font-medium-size);
  font-weight: 500;
  color: var(--primary-dark-color);
  margin-bottom: 2rem;
}

i {
  color: var(--secondary-main-color);
}

sources.src/sass/mixins.scss:
@mixin respond($breakpoint) {
  @if $breakpoint == tab-land {
    //tablet-landscape
    @media only screen and (max-width: 75rem) {
      @content;
    }
  }

  @if $breakpoint == tab-port {
    //tablet-portrait
    @media only screen and(max-width: 48rem) {
      @content;
    }
  }
  @if $breakpoint == mobile {
    //tablet-portrait
    @media only screen and(max-width: 37.5rem) {
      @content;
    }
  }
}

sources.src/sass/components.scss:
button {
  background: var(--primary-light-color);
  border: none;
  border-radius: var(--basic-radius);
  outline: none;
  padding: 0;
  cursor: pointer;
}
button:hover,
a:hover {
  transform: translateY(-2px);
}

ul {
  list-style: none;
  padding: 0;
}
a:link,
a:visited {
  text-decoration: none;
  color: inherit;
  cursor: pointer;
}

.spinner i {
  font-size: 3rem;
  color: var(--secondary-main-color);
  animation: rotate 2s infinite linear;
}

@keyframes rotate {
  0% {
    transform: rotate(0);
  }
  100% {
    transform: rotate(360deg);
  }
}

.error-message {
  margin: 1rem 0 0 1rem;
}

sources.src/sass/header.scss:
.header {
  background-color: var(--primary-dark-color);
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 6rem;
  padding: 0 1rem;
  border-radius: var(--basic-radius) var(--basic-radius) 0 0;
  @include respond(mobile) {
    flex-wrap: wrap;
    height: 7rem;
    padding: 0.75rem;
  }

  &__logo {
    display: flex;
    align-items: center;
    @include respond(mobile) {
      flex-basis: 100%;
    }
    &__img {
      width: 3rem;
    }
    &__text {
      font-size: var(--font-large-size);
      @include respond(mobile) {
        font-size: var(--font-regular-size);
      }
    }
  }

  &__search {
    font-size: var(--font-regular-size);
    display: flex;
    justify-content: center;
    @include respond(mobile) {
      flex-basis: 70%;
      margin: 0 auto;
      height: 100%;
    }

    &__input {
      width: 25rem;
      height: 2.5rem;
      border: none;
      border-radius: var(--basic-radius);
      font-size: inherit;
      font-family: inherit;
      padding: 0.5rem;
      margin-right: 0.25rem;

      @include respond(mobile) {
        width: 100%;
      }
    }
    &__btn {
      width: 5rem;
      height: 2.5rem;
      font-size: inherit;
      font-family: inherit;
    }
  }

  &__bookmarks {
    font-size: var(--font-regular-size);
    position: relative;
    height: 100%;

    &:hover &__content,
    &__content:hover {
      opacity: 1;
      visibility: visible;
    }
    &__btn {
      font-size: inherit;
      font-family: inherit;
      margin-top: 2.6rem;
      background: transparent;
      @include respond(mobile) {
        margin-top: 1rem;
      }
    }

    &__content {
      position: absolute;
      width: 20rem;
      background-color: var(--primary-light-color);
      top: 6rem;
      right: -1rem;
      opacity: 0;
      visibility: hidden;
      @include respond(mobile) {
        top: 3.75rem;
        right: -0.75rem;
      }
    }
    &__add-recipe {
      margin-right: 1rem;
    }
  }
}

sources.src/sass/list.scss:
.list {
  flex: 0 0 30%;
  background-color: var(--primary-light-color);
  &__pagination {
    position: relative;
    margin-bottom: 2rem;

    & button {
      width: 5rem;
      height: 2rem;
      border: 1px solid rgba(0, 0, 0, 0.12);
      @include respond(tab-port) {
        font-size: 1rem;
      }
    }
    &__btn--prev {
      position: absolute;
      left: 0;
    }
    &__btn--next {
      position: absolute;
      right: 0;
    }
  }
}
.recipe-list {
  margin-bottom: 2rem;
  &__item {
    padding: 1rem;
    margin: auto;
    font-size: var(--font-regular-size);

    &:hover {
      background-color: var(--primary-main-color);
    }
    &__link {
      display: flex;
      align-items: center;
    }
    &__img {
      width: 3rem;
      height: 3rem;
      margin-right: 1rem;
    }
    &__title {
      font-size: var(--font-regular-size);
    }
  }
}

sources.src/sass/recipe.scss:
.recipe {
  flex: 1;
  min-height: 100vh;
  background-color: var(--secondary-light-color);
  text-align: center;
  &__init-message {
    font-size: var(--font-medium-size);
    margin: 7rem;
  }
  &__img {
    width: 100%;
    height: 30rem;
  }

  &__info {
    position: relative;
    margin-bottom: 2rem;

    &__bookmark {
      position: absolute;
      top: 0;
      right: 4rem;
      background-color: transparent;
      font-size: var(--font-large-size);
    }
  }

  &__ingredients {
    text-align: center;
    margin: 2rem;
    &__list {
      display: flex;
      flex-wrap: wrap;
    }
    &__item {
      flex-basis: 50%;
      margin-bottom: 0.5rem;
    }
  }
  &__instructions {
    text-align: center;
    margin: 4rem 2rem;

    &__video {
      display: block;
      width: 15rem;
      height: 2.5rem;
      padding-top: 0.6rem;
      margin: auto;
      margin-top: 1rem;
      color: var(--primary-main-color);
      background-color: var(--primary-light-color);
      border-radius: var(--basic-radius);
    }
  }
}

@keyframes rotate {
  0% {
    transform: rotate(0);
  }
  100% {
    transform: rotate(360deg);
  }
}

